import sys

def read_credentials():
    username = input("Username: ")
    password = input("Password: ")
    return username, password

def main():
    if len(sys.argv)!= 2 or not sys.argv[1] in ["store", "dump"]:
        print("Usage: python script.py store/dump")
        sys.exit(1)

    if sys.argv[1] == "store":
        credentials = read_credentials()
        with open("creds.txt", "w") as f:
            f.write("{}\n{}".format(*credentials))
        print("Credentials stored in creds.txt")
    elif sys.argv[1] == "dump":
        try:
            with open("creds.txt", "r") as f:
                print("Username: {}\nPassword: {}".format(*f.read().split("\n")))
        except FileNotFoundError:
            print("No stored credentials found")
        except Exception as e:
            print("Error reading stored credentials: {}".format(e))

if __name__ == "__main__":
    main()
